mapper.insert:


INSERT
  INTO  LETTCCMMNDETAILCODE
     (  CODE_ID
     ,  CODE
     ,  CODE_NM
     ,  CODE_DC
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{codeId}
     ,  #{code}
     ,  #{codeNm}
     ,  #{codeDc}
     ,  #{useAt}
     ,  sysdate()
     ,  #{frstRegisterId}
     ,  sysdate()
     ,  #{frstRegisterId}
     )

mapper.update:


UPDATE  LETTCCMMNDETAILCODE
   SET  CODE_NM           = #{codeNm}
     ,  CODE_DC           = #{codeDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CODE_ID   = #{codeId}
   AND  CODE      = #{code}

mapper.delete:


UPDATE  LETTCCMMNDETAILCODE
   SET  USE_AT    = 'N'
 WHERE  CODE_ID   = #{codeId}
   AND  CODE      = #{code}
